REPLACE INTO `cp_itemdesc` VALUES(1472, '両手という<br/>致命的な短所を<br/>抱いているにも<br/>かかわらず<br/>魔力を増幅させてくれる<br/>強力な効果を持った<br/>優秀な杖。<br/>―――――――――――――<br/>Matk + 15%。<br/>―――――――――――――<br/>Int + 5、Agi + 2。<br/>―――――――――――――<br/>系列 : <font color="777777">両手杖</font><br/>攻撃 : <font color="777777">25</font><br/>重量 : <font color="777777">140</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">73</font><br/>装備 : <font color="777777">マジシャン系 ソウルリンカー</font>');
