REPLACE INTO `cp_itemdesc` VALUES(1530, '雷神トールが使ったと<br/>言われている<br/>巨大なハンマー。<br/>どこに投げても<br/>また手に戻ってきた<br/>という伝説がある。<br/>―――――――――――――<br/>Str + 20、Dex + 50。<br/>―――――――――――――<br/>無限攻撃ヘイスト。<br/>―――――――――――――<br/>物理攻撃時、<br/>一定確率で<br/>[サンダーストーム]Lv 10<br/>発動。<br/>―――――――――――――<br/>近接物理攻撃時、<br/>一定確率で<br/>武器が覚醒し、<br/>10秒間、<br/>スプラッシュダメージ。<br/>―――――――――――――<br/>系列 : <font color="777777">鈍器</font><br/>攻撃 : <font color="777777">350</font><br/>重量 : <font color="777777">600</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">95</font><br/>装備 : <font color="777777">ソードマン系 マーチャント系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1531, '全体が鋼鉄でできていて簡単には壊れない。単純な構造と高い活用度で愛されている。工具と同時に鈍器にもなる優れもの。<br/>スタン, 暗黒, 毒, 氷化の中の一つをランダムで打撃対象に付与する。<br/>系列 : <font color="777777">鈍器</font>    攻撃 : <font color="777777">115</font><br/>重量 : <font color="777777">250</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">55</font><br/>装備 : <font color="777777">アコライト系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1599, '知ろうとすると危ないアイテム');
REPLACE INTO `cp_itemdesc` VALUES(2109, 'マジシャンが使う魔法本でその大きさと厚さで充分に盾として利用することができる。<br/>INT + 1 , MDEF + 2<br/>系列 : <font color="777777">盾</font><br/>防御 : <font color="777777">3</font>   重量 : <font color="777777">100</font><br/>装備 : <font color="777777">マジシャン系 ソウルリンカー</font>');
REPLACE INTO `cp_itemdesc` VALUES(2410, 'オーディンが乗っていた<br/>という八本脚の馬の名前を<br/>つけた履き物で、<br/>魅力的な効果には<br/>誰もが履くことを<br/>願ってやまないだろう。<br/>―――――――――――――<br/>Int + 25、Mdef + 10。<br/>―――――――――――――<br/>MaxHP、MaxSP + 20%。<br/>SP回復力 + 25%。<br/>―――――――――――――<br/>無限移動ヘイスト。<br/>―――――――――――――<br/>破壊不可。<br/>―――――――――――――<br/>系列 : <font color="777777">靴</font><br/>防御 : <font color="777777">5</font><br/>重量 : <font color="777777">350</font><br/>要求レベル : <font color="777777">94</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(2629, '腰に着用すると<br/>「化け物か」と<br/>言われるほど<br/>力を劇的に<br/>引き上げてくれる<br/>アイテム。<br/>雷神トールの<br/>専用ベルトと<br/>いわれている。<br/>―――――――――――――<br/>Str + 40、Mdef + 7。<br/>―――――――――――――<br/>自分のBaseレベルが<br/>5上がる毎に<br/>Strが 1ずつ増加。<br/>―――――――――――――<br/>純粋なStrが<br/>120以上の時、<br/>ボスモンスターに与える<br/>物理ダメージ + 10%。<br/>―――――――――――――<br/>系列 : <font color="777777">アクセサリー</font><br/>防御 : <font color="777777">2</font><br/>重量 : <font color="777777">800</font><br/>要求レベル : <font color="777777">94</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5021, 'すべてを失って破産した<br/>商人の怨念が込められている<br/>という呪われたヘアバンド。<br/>着用した者は<br/>お金を失った者の心を<br/>感じることができる<br/>ようになり、<br/>悲しみで全身が包まれて<br/>絶望の淵に落ちたような<br/>感覚になるが、<br/>また立ち直ることができる<br/>という強烈な不屈の<br/>闘志によって着用者の能力が<br/>増幅される不思議な<br/>ヘアバンド。<br/>―――――――――――――<br/>Int + 1 , Dex + 1<br/>―――――――――――――<br/>[破産者の仮面]と<br/>共に装備時、<br/>[お金を失った者の心]の<br/>精錬値が1上がる度に<br/>無属性攻撃で<br/>受けるダメージ - 1%<br/>MaxHP + 100<br/>―――――――――――――<br/>系列 : <font color="777777">兜</font><br/>防御 : <font color="777777">4</font><br/>位置 : <font color="777777">上段</font><br/>重量 : <font color="777777">120</font><br/>要求レベル : <font color="777777">38</font><br/>装備 : <font color="777777">マーチャント系</font>');
REPLACE INTO `cp_itemdesc` VALUES(5022, '太陽を象徴する巨大な黄金の羽が引き立つ兜。これを被ってどうやって歩くかは考えない方がいいようだ。<br/>STR + 3 , INT + 2<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">4</font><br/>位置 : <font color="777777">上段/中段</font>   重量 : <font color="777777">240</font><br/>装備 : <font color="777777">2次職 上位2次職 3次職</font>');
REPLACE INTO `cp_itemdesc` VALUES(5024, '誕生日の時に被って歩けば、道行く人からも祝福を受けることができる帽子。模型なので食いついて怪我をする事がないようにしよう。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">1</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">100</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5025, '高い防御力を持ったヘルムに天使の羽毛を集めて作った羽を付けて製作されられた驚くべき兜。製作者が誰かはわからない。<br/>AGI + 1 , LUK + 1<br/>MDEF + 3<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">5</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">160</font><br/>要求レベル : <font color="777777">74</font><br/>装備 : <font color="777777">2次職 上位2次職 3次職</font>');
REPLACE INTO `cp_itemdesc` VALUES(5026, '飲食店でよく見られる、コックがかぶっている帽子。<br/>DEX + 1<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">1</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">30</font><br/>要求レベル : <font color="777777">50</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(7057, '吹けば天上界までその音が響いたと言われる巨大な角笛。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7058, '猫の足音、女人のひげ、石の根、魚の魂、鳥の唾、熊の筋等を交ぜて作ったと言う鎖。糸のように細くて軽いが如何なるものでも切れないほど強固だ。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7059, 'カプラ倉庫を一度無料で利用することができるようにする無料利用券。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7060, 'カプラ空間移動を一度無料で利用することができるようにする無料利用券。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7061, 'カプラカートを一度無料で利用することができるようにする無料利用券。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7062, '硬くて丈夫な亀の甲羅。刀でも切れなさそうな強靭さがすばらしい。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7063, 'まるで起きたくない朝に自分を取り囲んでいるやわらかな布団の誘惑のような感じを与える羽毛。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7064, '薄くて長くて軽いとんぼの羽を切り取ったもの。背中にはつけられないようだ。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7065, '服を作って着れば暖かそうなラッコの毛皮。見るだけでも柔らかさが感じられる。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7066, '細かくなった氷の固まり。溶けることが無いので売ることができる。不思議な氷片。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7067, '砕けた石の破片。美しい模様が入っていて石収集家に売ることができるようだ。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7068, '燃えている木の火を消したもの。どこに使えるかはわからないがとにかく売れる。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7069, '壊れてしまって、これ以上使われないことが確かな鎧。古鉄商人がほしがるかも知れない。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7070, '割れた亀の甲羅。予想外に高値で売れる。一時期、占星術にも使われたようだ。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7071, '着古されてぼろぼろになった服。誰が着たかは分からないが売れるらしい。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7072, 'さびついてしまった古い手裏剣。手裏剣マニアたちが密かに集めているようだ。<br/>重量 : <font color="777777">1</font>');
REPLACE INTO `cp_itemdesc` VALUES(7073, '女神フレイアの涙が固まってできたと言う神秘的な輝きを持つ宝石。眺めることだけでもうっとりしてしまう。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7074, '雷神トールが鎚を投げる時に着用したと言う鋼鉄の手袋。丈夫で実用的に見えるが着用することはできない。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7075, 'ある時代に女の人を拷問するために作られたと言われている残忍な道具。今でも彼女たちの悲鳴が聞こえてきそうだ。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7076, 'どこに使われるのかわからない歯車。使い道はありそうだが誰もわからないというのは問題だ。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7077, '銀でできている派手な装飾。いろいろな所に飾ってそれなりの趣をつくり出すことができる。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7078, '優秀な戦士を集めるヴァルキリーの怒った姿を職人がイメージ化して作り出した絶世の彫刻品。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7079, '天使が落としたと言われる羽毛。本物かどうかは判断することができない。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7080, '猫の歩く時の足音を集めて凝縮したという信じられないアイテム。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7081, '女性のひげを集めたもの。男性のものに見えるが、女性のものに間違いないだろう。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7082, '太古の昔に石ができてからあったという石の根。道端の石を一度裏返して見よう。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7083, '魚の魂を押しこめて封印したもの。透明な青みが漂っているのがすばらしく美しい。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7084, '鳥が流した唾を集めておいた長めの瓶<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7085, '強い力を出す熊の筋を集めて縛っておいたもの。とても弾力がある形態をしている。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7086, '太陽神を象徴する玉。ぴかぴかと光る黄金色がまぶしい。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7087, '魂が吹き出すという息づかい。生きた人が当たると体が凍ったような感覚になる。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7088, '空に舞う雪を集めて巨大な結晶にしたもの。常に六角形をしている雪の結晶は見る人の心を和ませて暖かくしてくれる。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7089, '力強い嵐を予告でもするかのようなイメージを持った瓶。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7090, 'よく晴れた日の海辺で足をくすぐるような波のイメージを持った瓶。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7091, '悪天候の日の海岸に打ちつける荒波のイメージを持った彫像。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(7092, '誰にもわからないが、いつも動いている空気を感じた職人が作ったと言われている。<br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(9025, 'キューペット、ボンゴンが眠っている卵。[携帯卵孵化機]で眠っているモンスターを目覚めさせる。<br/>種類 : <font color="777777">モンスターの卵</font><br/>重量 : <font color="777777">0</font>');
REPLACE INTO `cp_itemdesc` VALUES(10020, '墓所を守り、死者を導く道士が使ったと言われているもので、武器として使うには不適切だ。<br/>種類 : <font color="777777">キュ−ペット装備</font><br/>装備 : <font color="777777">ボンゴン</font><br/>重量 : <font color="777777">0</font>');
REPLACE INTO `cp_itemdesc` VALUES(2199, '知ろうとすれば怪我をするアイテム');
REPLACE INTO `cp_itemdesc` VALUES(5027, '才能ある魔術師なら誰もが欲する帽子。見た目はちょっとごつごつした感じがするが、かぶり心地はなかなかさわやかな感じがする帽子だ。<br/>INT + 2 , SP + 150<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">1</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">30</font><br/>装備 : <font color="777777">マジシャン系 ソウルリンカー</font>');
REPLACE INTO `cp_itemdesc` VALUES(5028, '熱くてどろどろと溶けている蝋燭は特定の人に妙な感覚を与える。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">0</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">15</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5029, 'きのこのモンスター「スポア」になりたい人には積極的にお勧めしたい帽子。装備してきのこの感じを味わおう！<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">3</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">90</font><br/>要求レベル : <font color="777777">20</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5030, '装備した瞬間、笹を手に<br/>している自分を見つける<br/>ことができそうなパンダに<br/>似ている帽子。<br/>とても可愛くて<br/>女性の人気が高い。<br/>―――――――――――――<br/>系列 : <font color="777777">兜</font><br/>防御 : <font color="777777">3</font><br/>位置 : <font color="777777">上段</font><br/>重量 : <font color="777777">80</font><br/>要求レベル : <font color="777777">40</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5031, '暗い所でもちゃんと見ることができるようにヘッドライトが取り付けられている工事帽。かなりの石頭とぶつかっても大丈夫な頑丈さが売りだ。<br/>DEX + 2<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">4</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">150</font><br/>要求レベル : <font color="777777">55</font><br/>装備 : <font color="777777">ソードマン系 アコライト系 マーチャント系 シーフ系</font>');
REPLACE INTO `cp_itemdesc` VALUES(5032, '日差しが強いとき、光を遮って日に焼けることを防ぐことができるステキな帽子。雨の日に被るとちょっと大変そうだ。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">1</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">80</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5033, '狸になりたがる人のために作られた帽子で製作者はわからない。しかし意外に男性に人気爆発であるようだ。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">3</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">90</font><br/>要求レベル : <font color="777777">50</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5034, '装備するとまるで頭が輝くように見えるヘアバンド。しかし光を反射しているわけではないので安心しよう。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">0</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">50</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5035, '人気絶頂のモンスター「ポリン」の姿を真似して作った帽子。材質は絶対にゴム手袋ではない！<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">2</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">70</font><br/>要求レベル : <font color="777777">38</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5036, '上に十字架のアクセサリーが付いているヘアバンド。軽くて可愛い。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">1</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">25</font><br/>要求レベル : <font color="777777">10</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5037, '堅い実を半分に切って作られた帽子。丈夫で優秀な防御力を持っている。しかし鎚で叩くとこわれるから精錬はできない。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">4</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">15</font><br/>要求レベル : <font color="777777">5</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5038, '子悪魔デビルチを真似して作った帽子。着心地がさわやかで可愛くて、着用した人はもちろん見る人々にも楽しさを与える。<br/>STR + 1 , INT + 1<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">2</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">80</font><br/>要求レベル : <font color="777777">64</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5039, 'なんの卵かわからないまだら模様の卵で作られた帽子。思いのほかゆとりがあり、頭が多少大きくても安心して着用することができる。鎚で叩くとこわれるから精錬はできない。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">4</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">40</font><br/>要求レベル : <font color="777777">19</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5040, '恋人に可愛く見せたい場合につけよう。美少女/美少年たちには必須アイテムだと言っても過言ではない……？<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">0</font><br/>位置 : <font color="777777">中段</font>   重量 : <font color="777777">10</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5041, '愛を表すハートのマークを組み合わせて作られた可愛いヘアピン。着用者をかわいく見せる力がある。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">0</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">10</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5042, '髪の毛をまとめて巻いた後に被せるもの。着用すると東方の雰囲気を醸し出すことが出来る。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">0</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">15</font><br/>要求レベル : <font color="777777">14</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5043, 'ある不運な主人公がかぶったと言われている仮面。じっと見ていていたら主人公の痛みが体中に伝わって来そうだ。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">1</font><br/>位置 : <font color="777777">中段</font>   重量 : <font color="777777">20</font><br/>要求レベル : <font color="777777">20</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5044, '広くひらかれたデザインが特徴な羽。着用してもその羽で飛んでいくことは無理そうだ……<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">2</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">35</font><br/>要求レベル : <font color="777777">45</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5045, '手を入れればすぐにでも兎が飛び出しそうな手品師の帽子。結構丈夫で、簡単には壊れない。<br/>DEX + 1 , AGI + 1 , SP + 50<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">3</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">50</font><br/>要求レベル : <font color="777777">50</font><br/>装備 : <font color="777777">マジシャン系 アコライト系 ソウルリンカー</font>');
REPLACE INTO `cp_itemdesc` VALUES(5046, 'モンスター「ボンゴン」が被っている帽子。ポイントは前の半分破れた御札だ。とても丈夫で、破れた御札のおかげで視野が狭くなることもないようだ。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">5</font><br/>位置 : <font color="777777">上,中,下段</font>   重量 : <font color="777777">30</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5047, 'さわやかな着用感と視線集中効果において想像以上の能力を発揮するサングラス。それなりにセンスを持った人がつければ素敵に見える。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">0</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">10</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
