REPLACE INTO `cp_itemdesc` VALUES(5050, '神秘の木の実を半分に切って中を繰り出して頭にかぶれるようにしたもの。木の実なのに丈夫なのはそれが神秘の木の実だからだ。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">5</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">30</font><br/>要求レベル : <font color="777777">30</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5051, '振る度に心地よい音が鳴る鈴。首に付けることができ、つけた人をとても可愛く見せる特徴がある。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">0</font><br/>位置 : <font color="777777">下段</font>   重量 : <font color="777777">10</font><br/>装備 : <font color="777777">全ての職業</font>');
