REPLACE INTO `cp_itemdesc` VALUES(1916, 'このギターを持つと、岩と会話をすることができると言われている。だが、岩と会話をしていると、独りになりがちなのでやめよう。<br/>系列 : <font color="777777">楽器</font>  攻撃 : <font color="777777">110</font><br/>重量 : <font color="777777">90</font>    属性 :<font color="777777">地</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">27</font><br/>装備 : <font color="777777">バード系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1917, '音楽を演奏すればどこかで風が吹いて来て、急に旅に出たくなるというギター。<br/>系列 : <font color="777777">楽器</font>  攻撃 : <font color="777777">110</font><br/>重量 : <font color="777777">90</font>    属性 :<font color="777777">風</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">27</font><br/>装備 : <font color="777777">バード系</font>');
