REPLACE INTO `cp_itemdesc` VALUES(1968, '取っ手が木になっている縄跳び。あの取っ手で頭を打たれたらじいんとして涙がぐみそうだ。<br/>クリティカル確率20%増加。<br/>系列 : <font color="777777">鞭</font>  攻撃 : <font color="777777">120</font><br/>重量 : <font color="777777">40</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">30</font><br/>装備 : <font color="777777">ダンサー系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1969, '砕いた刃をワイヤーで通して鞭の形状にした、切れ味が抜群の鞭。ほとんど剣に近い。<br/>攻撃時、敵に3%の確率で出血を起こす。<br/>系列 : <font color="777777">鞭</font>  攻撃 : <font color="777777">140</font><br/>重量 : <font color="777777">120</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">30</font><br/>装備 : <font color="777777">ダンサー系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1970, '高い攻撃力を誇る鞭。危ない想像は禁物。<br/>アローバルカン、矢撃ちの攻撃力10%増加。<br/>系列 : <font color="777777">鞭</font>  攻撃 : <font color="777777">150</font><br/>重量 : <font color="777777">110</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">65</font><br/>装備 : <font color="777777">ダンサー系</font>');
REPLACE INTO `cp_itemdesc` VALUES(2113, 'ノービスのために特別製作された、特殊な金属でできたかわいいシールド。<br/>無属性を除いた全属性のダメージ20%減少。<br/>系列 : <font color="777777">盾</font>   防御 : <font color="777777">3</font><br/>重量 : <font color="777777">100</font><br/>要求レベル : <font color="777777">40</font><br/>装備 : <font color="777777">ノービス系</font>');
REPLACE INTO `cp_itemdesc` VALUES(2114, 'トールが巨人族の王フルングニルを殺して、その時手に入れた石で作ったという盾。神々と対立した巨人たちの底力が感じられる。<br/>大型モンスターから受けるダメージ5%減少。<br/>オーディンの祝福、マグニキャップと共に装備時、STR + 2, DEF + 5, MDEF + 5。 ソードマン系装備時 Def+ 6。<br/>系列 : <font color="777777">盾</font>   防御 : <font color="777777">3</font><br/>重量 : <font color="777777">150</font><br/>要求レベル : <font color="777777">65</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(2115, 'フリッグの侍女である<br/>女神グナーが<br/>ヴァルキリー時代の戦闘で<br/>使った盾。<br/>神の戦士ヴァルキリーの<br/>闘魂が感じられる。<br/>―――――――――――――<br/>Mdef + 5<br/>―――――――――――――<br/>火・水・闇・不死属性攻撃で<br/>受けるダメージ - 20%<br/>―――――――――――――<br/>[オーディンの祝福]、<br/>[フリッグのサークレット]と<br/>共に装備時、追加で<br/>Def + 2 , Mdef + 5<br/>[フリッグのサークレット]の<br/>精錬値が1上がる度に追加で<br/>Def - 1 , Mdef + 1<br/>[ヴァルキリーシールド]の<br/>精錬値が1上がる度に追加で<br/>Def - 1 , Mdef + 1<br/>―――――――――――――<br/>[ロングホーン]と<br/>共に装備時、追加で<br/>物理攻撃を受けた時、<br/>一定確率で<br/>自分にオートスペル<br/>[アスムプティオ]Lv2発動<br/>―――――――――――――<br/>系列 : <font color="777777">盾</font><br/>防御 : <font color="777777">3</font><br/>重量 : <font color="777777">50</font><br/>要求レベル : <font color="777777">65</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(2116, 'スーパーノービスの守護天使の盾。悪を退ける神聖な力が込められている。<br/>悪魔形モンスターからのダメージ5%減少。<br/>天使の加護、天使の再来、天使の守護、天使の温もり、天使の口づけをすべて装備時、<br/>HP + 900、 SP + 100<br/>物理攻撃を受けた場合低確率で自分にアスムプティオLv1をかける。<br/>系列 : <font color="777777">盾</font>   防御 : <font color="777777">3</font><br/>重量 : <font color="777777">40</font><br/>要求レベル : <font color="777777">20</font><br/>装備 : <font color="777777">ノービス系</font>');
REPLACE INTO `cp_itemdesc` VALUES(2353, 'ヘルモードセットの基本になる鎧。オーディンの祝福は特定の防具と共に装備すると、隠された力が引き出される。<br/>系列 : <font color="777777">鎧</font>   防御 : <font color="777777">6</font><br/>重量 : <font color="777777">250</font><br/>要求レベル : <font color="777777">65</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
