REPLACE INTO `cp_itemdesc` VALUES(2416, '冒険初心者のために特別製作されたかわいいシューズ。<br/>MAX HP 5% 増加。<br/>系列 : <font color="777777">靴</font>   防御 : <font color="777777">2</font><br/>重量 : <font color="777777">50</font><br/>要求レベル : <font color="777777">40</font><br/>装備 : <font color="777777">ノービス系</font>');
