REPLACE INTO `cp_itemdesc` VALUES(2419, 'ゴヴニュが、ライバルであるドルブとの賭のために作ったという靴。<br/>MDEF + 3, MHP + 5%, MSP + 5%。<br/>ゴヴニュの兜・ゴヴニュの鎧・ゴヴニュの肩飾り・ゴヴニュの軍靴を全て装備すると、VIT + 5, MHP + 15%, MSP + 5%, DEF + 5, MDEF + 15, 水、風、地、火属性に対する耐性10%向上。<br/>系列 : <font color="777777">靴</font>   防御 : <font color="777777">4</font><br/>重量 : <font color="777777">70</font><br/>要求レベル : <font color="777777">54</font><br/>装備 : <font color="777777">ノービス系を除く全ての職業</font>');
