REPLACE INTO `cp_itemdesc` VALUES(2669, '何かの行事を記念するために作られた金のネックレス。「RJC」という文字が刻まれている。<br/>SP + 10<br/>系列 : <font color="777777"> アクセサリー</font><br/>防御 : <font color="777777">0</font>   重量 : <font color="777777">10</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5151, '音符の模様が格好良いヘッドフォン。どこにいても楽しい音楽を聴くことができる。<br/>スタンに対する耐性+10%<br/>凍結に対する耐性+10%<br/>系列 : 兜<br/>防御 : 4<br/>位置 : 上中段<br/>重量 : 20<br/>装備 : 全ての職業');
REPLACE INTO `cp_itemdesc` VALUES(5132, 'すべてのポリンたちのお母さんであるエンジェリングを模して作った帽子。人間から種族を保護しようとする心が込められている。<br/>人間形モンスターからのダメージ 10% 減少。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">2</font><br/>位置 : <font color="777777">上段</font>   重量 : <font color="777777">70</font><br/>要求レベル : <font color="777777">38</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(12041, '料理初心者でも簡単に作ることができる料理。青い草の味がしそうだ。<br/>STR + 1<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">6</font>');
REPLACE INTO `cp_itemdesc` VALUES(12042, '少し見た目が気持ち悪いかもしれないが、試食すると新しい世界に出会えるようになる珍しい料理。<br/>STR + 2<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12043, '平凡なステーキのように見えるが、食べたらまるで爆発するような気がする強烈な料理。<br/>STR + 3<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12044, 'フェイヨンに伝わる秘料理法で、ハーブを添加し、より香ばしくなった味付けカルビ。<br/>STR + 4<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12045, 'タシンチェおばさんの手作りの味が感じられる甘口のチヂミ。元気が出てくるような味だ。<br/>STR + 5<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12046, 'ブドウの果汁にハーブの香りを添加し、頭がシャキッとする香りの良いお茶。ただのブドウジュースみたいな気もする。<br/>INT + 1<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">10</font>');
REPLACE INTO `cp_itemdesc` VALUES(12047, '3種類のハーブの香りをバランス良く合わせた香りを楽しむお茶。味はまあまあだ。<br/>INT + 2<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12048, 'ハーブティーに、疲労回復に良いハチ蜜を混ぜし、甘く仕上げたお茶。熱いので、気を付けて飲もう。<br/>INT + 3<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12049, 'モロクのある商人が開発したお酒。やや酸味のある果物を漬けたお酒で、女性も気軽に楽しめる味。<br/>INT + 4<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">30</font>');
REPLACE INTO `cp_itemdesc` VALUES(12050, 'マステラから頭をすっきりさせてくれる効果のみ抽出して極大化させたお酒。飲みすぎは禁物。<br/>INT + 5<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">40</font>');
REPLACE INTO `cp_itemdesc` VALUES(12051, 'ハーブとカニを一緒に煮る簡単な料理。殻をうまくむいて食べるのが重要だ。<br/>VIT + 1<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">10</font>');
REPLACE INTO `cp_itemdesc` VALUES(12052, '海の近くが故郷である人々に、深い郷愁を与える料理。思わず涙が出てくるという。<br/>VIT + 2<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12053, '海の香りが生きているおいしい料理。汁と貝を全部食べることができる。<br/>VIT + 3<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">30</font>');
REPLACE INTO `cp_itemdesc` VALUES(12054, '体に良いイカ墨とハーブを入れ、触手を軽く煮た料理。一見おいしそうに見えるが、実は微妙な味。<br/>VIT + 4<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12055, '餃子にやや辛めの味付けをして焼き上げた、食欲のそそる料理。たくさん食べると、翌日地獄を経験する。<br/>VIT + 5<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">80</font>');
REPLACE INTO `cp_itemdesc` VALUES(12056, '墨汁にかえるの卵を入れ、弱火で煮たスープ。目をつぶって一気に飲んだほうが良さそうだ……。<br/>AGI + 1<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">10</font>');
REPLACE INTO `cp_itemdesc` VALUES(12057, '麺の職人が作ったような滑らかさが命である麺。つるつるして食べにくい。<br/>AGI + 2<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">10</font>');
REPLACE INTO `cp_itemdesc` VALUES(12058, '滑らかな触手を甘いさつまいもと香ばしいチーズと一緒に焼いた薄黄色のグラタン。子供のおやつにありそうな味とはちょっと違うらしい。<br/>AGI + 3<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12059, '冷たさとピリ辛が絶妙にマッチした味。カボチャが料理の飾りとして添えられているのが珍しい。<br/>AGI + 4<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">40</font>');
REPLACE INTO `cp_itemdesc` VALUES(12060, '神秘の食材料である赤いコウモリの翼をヒナレの葉に包んだ後、中身をくり抜いたカボチャに入れて充分に煮込んだ栄養食。微妙な味がする。<br/>AGI + 5<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12061, 'ブドウジュースに栄養価の多いハチ蜜を入れて作った特製ブドウジュース。甘酸っぱい味がさわやかでおいしい。<br/>DEX + 1<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">10</font>');
REPLACE INTO `cp_itemdesc` VALUES(12062, '王宮デザート職人「シャルルオルレアン」も絶賛したという特製チョコレートムースケーキ。柔らかくてほろ苦い味がする。<br/>DEX + 2<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12063, '色々な果物を食べやすく切り、シロップにつけたデザート。サクサクした食感が一品だ。<br/>DEX + 3<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12064, '焼きたてのパンに刻んだ肉とクリームチーズを入れた淡泊なサンドイッチ。お昼の人気メニューのようだ。<br/>DEX + 4<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12065, '健康に良い野菜に甘いウンバラ風ソースを混ぜたサラダ。プロンテラで流行りの最新健康志向メニュー。<br/>DEX + 5<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12066, 'サルのしっぽを黄金色に炒めて基本的な味付けをした簡単な料理。おつまみに人気という噂だ。<br/>LUK + 1<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">6</font>');
REPLACE INTO `cp_itemdesc` VALUES(12067, '色々なフルーツジュースを絶妙なバランスでミックスした混合ジュース。割合を間違えると新世界を経験することがある。<br/>LUK + 2<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12068, 'さつまいもを揚げた後、シロップを入れて煮詰めた料理。サクとした食感と甘みがおやつに大人気。<br/>LUK + 3<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12069, '獲ったばかりのイキの良い魚と、なぜか健康に良いという噂が広がってしまった古代魚の唇を、良い香りの木の葉に包んで蒸した魚料理。<br/>女性より男性に人気と言う。<br/>LUK + 4<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12070, '味が逸品いわれるサソリのしっぽ部分だけを炒めた料理。硬い殻をむいて柔らかい肉だけを食べる。微弱だが毒があり、食べ過ぎは体に良くない。<br/>LUK + 5<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">40</font>');
REPLACE INTO `cp_itemdesc` VALUES(12071, 'あまりにも素晴らしい味の焼き肉なので、食べた人々の間で「輝くたれ付き焼き肉」と呼ばれている。<br/>STR + 6<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">80</font>');
REPLACE INTO `cp_itemdesc` VALUES(12072, '辛口ソースを塗った大きな肉を丸ごと焼いた料理。お肉が好きな人には最高の料理らしい。<br/>STR + 7<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12073, '龍之城の最高の珍味料理で、特に右足を使ったものが人気だと言われている。<br/>STR + 8<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12074, '新鮮で質のよい筋肉を短時間で焼いて、食材の味をそのまま生かした料理。<br/>STR + 9<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12075, '材料が高いほど柔らかくておいしい最高級料理。龍之城から渡って来た料理と言われている。<br/>STR + 10<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12076, 'ブドウを熟成させて作ったお酒だが、通常のワインとは全然違う味がするという。秘密はキノコに隠されている。<br/>INT + 6<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">30</font>');
REPLACE INTO `cp_itemdesc` VALUES(12077, 'ハーブティーにローヤルゼリーとハチ蜜を加え、精神力の回復に良い飲み物となった。香りは良くないという。<br/>INT + 7<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">80</font>');
REPLACE INTO `cp_itemdesc` VALUES(12078, 'ティータイムの甘い香りがお城の外まで広がるという王宮デザート職人「シャルル・オルレアン」の野心作。<br/>INT + 8<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12079, 'トリスタン3世の即位を記念して製造された最高級のお酒で、12年産の味が一番良いと言う。<br/>INT + 9<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12080, '一口だけでも口の中で竜の息が感じられるような強烈な味。一度この味を味わった者はこの味を絶対に忘れることができないという。<br/>INT + 10<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12081, '薬と言うのはまずいほど効果が高いという未確認情報がある。<br/>VIT + 6<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12082, '食べると、頭の中に巨大な山と青い海が同時に描かれ、涙が出てくるという不思議な料理。<br/>VIT + 7<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">40</font>');
REPLACE INTO `cp_itemdesc` VALUES(12083, '絶対に一口で食べられないように、わざと大きくしたような巨大な包み料理。作った人の気持ちがよく伝わってくる。<br/>VIT + 8<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12084, '主材料が「ドラゴン」である辛くてピリピリする料理。食べたら昇天しそうな感じがすると言う。<br/>VIT + 9<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12085, '見た目と香りがちょっとあれだが、これを食べれると不死身になった気がするという。<br/>VIT + 10<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12086, 'チリの辛い味と海老の香ばしい味がよく調和したグラタン……と言うよりただの辛い海老料理という感じ。<br/>AGI + 6<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">80</font>');
REPLACE INTO `cp_itemdesc` VALUES(12087, '焼いたワニの肉を野菜で包んで蒸したウンバラ酋長のお勧め料理。大事なお客様と、育てている家畜にだけ食べさせるという妙なうわさがある。<br/>AGI + 7<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">80</font>');
REPLACE INTO `cp_itemdesc` VALUES(12088, '普通の激辛カレーに「忍耐の味」という調味料を添加したものらしい。隠し味のポイントは、頭にかぶっていたようなカボチャを使ったとか……。意味不明である。<br/>AGI + 8<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12089, '色んな種類の肉を弱火でじっくり煮こんだ料理。ふたを開けると香りが周囲に広がるという龍之城の名物。<br/>AGI + 9<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12090, 'ソグラト砂漠の4大珍味に数えられる砂漠サソリの茹で料理。砂の熱を利用して茹でると言う。<br/>AGI + 10<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12091, 'コンロン特産の桃で作った、香りも味もさわやかなケーキ。このケーキの桃はとても軟らかい。<br/>DEX + 6<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">80</font>');
REPLACE INTO `cp_itemdesc` VALUES(12092, '魂が飛んでしまいそうな甘口のパン。舌が溶けてしまいそうだという話も広がっている。<br/>DEX + 7<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">80</font>');
REPLACE INTO `cp_itemdesc` VALUES(12093, '肉と果物、チーズを乗せた普通の3倍もありそうな巨大トースト。材料が豊富入っているといっても味がどうかは不明。<br/>DEX + 8<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12094, '口にしたすべての人が「空でも飛べそうな感じだった」と言うジュース。一番重要なのは果物の適量配分らしい。<br/>DEX + 9<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12095, '全ての川の水の源泉と呼ばれる伝説の泉の名を付けられるほど、飲んだ人皆がこのお酒を称賛したと言われている。<br/>DEX + 10<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12096, 'ものすごく熱い時に飲むと巨大な運が付くという。しかし、それはただの噂のようだ。<br/>LUK + 6<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">30</font>');
REPLACE INTO `cp_itemdesc` VALUES(12097, '色んな肉を長く細く整えた木に刺し、焼いて食べる料理。材料を好きなように選べるので、人気満点。<br/>LUK + 7<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">80</font>');
REPLACE INTO `cp_itemdesc` VALUES(12098, 'さわやかないちごの味と香りが逸品のおにぎり。いちごを具にするなど、どこでもできない珍しい経験。<br/>LUK + 8<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">40</font>');
REPLACE INTO `cp_itemdesc` VALUES(12099, 'シュワッと痺れる舌触りが子供達に人気の炭酸ソーダ。少々血の味がするが、おいしいので気にならない。<br/>LUK + 9<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">100</font>');
REPLACE INTO `cp_itemdesc` VALUES(12100, 'ピリッっとした九尾狐のしっぽ料理。ルーンミッドガッツには幸運の料理としても広く知られている。<br/>LUK + 10<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">50</font>');
REPLACE INTO `cp_itemdesc` VALUES(12103, '幾年の歳月が経ち、<br/>魔力が秘められた木の枝に<br/>血の契約をし、<br/>強力な何かを召喚する。<br/><font color="FFFFFF">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12104, '矢筒の中身に関する表記が消され、どの矢が出るか分からない。<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12105, 'きれいに包まれたテイミングアイテムセット。プレゼント用のテイミングアイテムとテイミング装備が入っている。<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12106, 'アクセサリーが入っているきれいな宝石箱。<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12107, '素敵に包装された仮面。どの仮面が入っているのか開けて確かめてみよう。<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12108, '数枚の呪文書がくるくる巻かれた包み。ちょっと古く見える。<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12109, '包装にポリン模様の印が<br/>押されている箱。<br/>中で何か動く音がする。<br/>―――――――――――――<br/><font color="FF0000">以下の専用マップでのみ<br/>アイテムを使用した場合、<br/>モンスターが召喚されます<br/>　<br/>■専用マップ<br/>サモンパーク</font><br/>―――――――――――――<br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12110, '緊急時に使えるようにポーションが入っている箱。たまに応急処置用ではない無茶なものも入っている。<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12111, '食べ物を風呂敷で包装しておいたもの。<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">20</font>');
REPLACE INTO `cp_itemdesc` VALUES(12112, 'モロクの名物で、様々な果物を熟成させて作った甘くアルコールが強い飲み物。砂漠の気候で最もおいしく熟成できるらしい。<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">10</font>');
REPLACE INTO `cp_itemdesc` VALUES(12113, '濃い朱色の、明るくて軽快な色合いは海に良く合う。女性たちにも人気らしい。<br/><font color="ffffff">_</font><br/>重量 : <font color="777777">10</font>');
