REPLACE INTO `cp_itemdesc` VALUES(13026, '<font color="000088">強化装備アイテム</font><br/>使用期限になると、アイテムが削除されます。<br/>月光の精気を受けた剣。攻撃対象の精神力を吸収する。<br/>MAX SP 10% 上昇<br/>攻撃命中時、毎回 3のSPを回復する。<br/>系列 : <font color="777777">短剣</font>    攻撃 : <font color="777777">85</font><br/>重量 : <font color="777777">70</font><br/>武器レベル : <font color="777777">4</font><br/>装備 : <font color="777777">ソードマン系 マジシャン系 アーチャー系 マーチャント系 シーフ系 ソウルリンカー 忍者系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1534, '<font color="000088">強化装備アイテム</font><br/>使用期限になると、アイテムが削除されます。<br/>全体が鋼鉄でできていて簡単には壊れない。単純な構造と高い活用度で愛されている。工具と同時に鈍器にもなる優れもの。<br/>スタン、暗黒、毒、氷化の中の一つをランダムで打撃対象に付与する。<br/>系列 : <font color="777777">鈍器</font>    攻撃 : <font color="777777">150</font><br/>重量 : <font color="777777">250</font><br/>武器レベル : <font color="777777">3</font><br/>装備 : <font color="777777">アコライト系</font>');
REPLACE INTO `cp_itemdesc` VALUES(5283, '可愛いひよこを頭の上に乗せた帽子。<br/>ぴよぴよと動き回るのが非常に可愛い。<br/>まだ小さいが、一緒に戦ってくれる。<br/>人間形と動物形モンスターに対する耐性 3% 増加。<br/>精錬不可。<br/>MHP、MSP + 50<br/>Luk + 2<br/>全ての武器でダブルアタックLv2が使用可能。<br/>ダブルアタックLv3以上を習得している場合、全ての武器で習得Lvのダブルアタックが使用可能となる。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">1</font><br/>位置 : <font color="777777">上段</font><br/>重量 : <font color="777777">10</font><br/>要求レベル : <font color="777777">10</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5284, '頭に睡蓮の花を乗せているように見える帽子。<br/>なぜかわからないが、心が落ち着く気がする。<br/>DEX +1、AGI + 1、MDEF + 3<br/>HP 回復量 5% 増加。<br/>SP 回復量 3% 増加。<br/>精錬不可<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">0</font><br/>位置 : <font color="777777">上段</font><br/>重量 : <font color="777777">20</font><br/>要求レベル : <font color="777777">30</font><br/>装備 : <font color="777777">全ての職業</font>');
REPLACE INTO `cp_itemdesc` VALUES(5285, 'くるくる回る風車のかんざし。<br/>これを身につけていれば、いつもより風を感じやすくなるだろう。<br/>AGI + 2<br/>精錬不可。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">3</font><br/>位置 : <font color="777777">上段</font><br/>重量 : <font color="777777">30</font><br/>要求レベル : <font color="777777">30</font><br/>装備 : <font color="777777">全ての職業</font>');
