REPLACE INTO `cp_itemdesc` VALUES(5287, 'バカンスに行くならこの帽子。<br/>強い日の光から肌を守ってくれる。<br/>VIT + 1<br/>精錬不可。<br/>系列 : <font color="777777">兜</font>   防御 : <font color="777777">1</font><br/>位置 : <font color="777777">上段</font><br/>重量 : <font color="777777">20</font><br/>要求レベル : <font color="777777">30</font><br/>装備 : <font color="777777">全ての職業</font>');
