REPLACE INTO `cp_itemdesc` VALUES(1376, 'どんな鎧でも衝撃を<br/>そのまま伝える両手斧。<br/>特殊加工された刃は、<br/>何でも破壊できる。<br/>―――――――――――――<br/>Critical + 20<br/>攻撃速度 + 5%<br/>--------------------------<br/>精錬値が高い程、追加で<br/>　Criticalが増加。<br/>--------------------------<br/>ホワイトスミス、<br/>メカニック、<br/>クリエイター、<br/>ジェネティックの<br/>いずれかが<br/>装備時、追加で<br/>　物理攻撃時、一定確率で<br/>　ハンマーフォールLv3<br/>　発動。<br/>――――――――――――<br/>系列 : <font color="777777">斧</font><br/>攻撃 : <font color="777777">175</font><br/>重量 : <font color="777777">200</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">転生ソードマン系 転生マーチャント系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1377, 'ハリケーンの力を持つ<br/>巨大な両手斧。<br/>斧の先端にあるオーブには<br/>ハリケーンの力が封じられて<br/>おり、武器が強くなる程、<br/>オーブの力も強くなると<br/>言われている。<br/>―――――――――――――<br/>中型モンスターからの<br/>攻撃耐性 + 10%<br/>物理攻撃時、一定確率で<br/>自分を中心として、<br/>15x15セル範囲内の<br/>全ての敵に一定のダメージを<br/>与えると共に、<br/>7セルノックバックさせる。<br/>--------------------------<br/>精錬値が高い程、追加で、<br/>　中型モンスターからの<br/>　攻撃耐性が増加。<br/>--------------------------<br/>精錬値が高い程、追加で<br/>　攻撃速度が増加。<br/>―――――――――――――<br/>系列 : <font color="777777">斧</font><br/>攻撃 : <font color="777777">332</font><br/>重量 : <font color="777777">350</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">80</font><br/>装備 : <font color="777777">転生ソードマン系、転生マーチャント系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1477, '妖怪に復讐するために<br/>製作者が自分の身を<br/>犠牲にして作った<br/>強力な両手槍。<br/>槍に製作者の意志が<br/>宿っている為、<br/>使用者の意識を支配しようと<br/>するらしい。<br/>―――――――――――――<br/>闇属性・悪魔形・<br/>不死形モンスターに<br/>与えるダメージ + 20%<br/>悪魔形・不死形モンスター<br/>からの攻撃耐性 + 10%<br/>闇属性攻撃耐性 + 10%<br/>敵を倒した時、HP50回復。<br/>物理攻撃時、一定確率で<br/>自分を混乱状態にする。<br/>--------------------------<br/>封印の赤い絹と共に<br/>装備すると、<br/>　自分が混乱状態になる<br/>　確率減少。<br/>―――――――――――――<br/>系列 : <font color="777777">槍</font><br/>攻撃 : <font color="777777">170</font><br/>重量 : <font color="777777">200</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">75</font><br/>装備 : <font color="777777">ソードマン系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1478, '鋭利な先端をした槍。<br/>鎧の隙間に刺し込み、<br/>致命傷を与える事が出来る。<br/>―――――――――――――<br/>敵の防御力無視。<br/>人間形モンスターに<br/>与えるダメージ + 10%<br/>物理攻撃時、一定確率で<br/>ピアースLv5発動。<br/>―――――――――――――<br/>系列 : <font color="777777">槍</font><br/>攻撃 : <font color="777777">120</font><br/>重量 : <font color="777777">100</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">65</font><br/>装備 : <font color="777777">ナイト系 クルセイダー系</font>');
REPLACE INTO `cp_itemdesc` VALUES(2000, '全てを破壊する<br/>恐ろしい力が<br/>封じられた両手杖。<br/>使用者は杖が<br/>強くなればなるほど、<br/>暴走しようとする杖の力を<br/>抑える為に精神力の<br/>一部を犠牲に<br/>しなければならない。<br/>（未実装）<br/>―――――――――――――<br/>Matk + 25%。<br>―――――――――――――<br/>Int + 3、Agi + 10。<br/>―――――――――――――<br/>魔法力増幅の<br/>キャスティングタイム<br/>- 50%。<br/>--------------------------<br/>精錬値 2毎に追加で<br/>Matk + 1%。<br/>--------------------------<br/>精錬値が高くなる程、<br/>追加でSP消費率が増加。<br/>物理攻撃を受けた時、<br/>一定確率で<br/>[ユピテルサンダー]Lv 5<br/>が発動する確率が増加。<br/>―――――――――――――<br/>系列 : <font color="777777">両手杖</font><br/>攻撃 : <font color="777777">130</font><br/>重量 : <font color="777777">250</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">80</font><br/>装備 : <font color="777777">ハイウィザード ウォーロック</font>');
REPLACE INTO `cp_itemdesc` VALUES(2001, '二つの杖に分離された<br/>両手で持つタイプの杖で、<br/>交差させて十字架を作ると<br/>聖なる力を感じられる。<br/>―――――――――――――<br/>Dex + 4<br/>Matk + 15%<br/>―――――――――――――<br/>不死形・悪魔形モンスター<br/>から受けるダメージ - 15%<br/>―――――――――――――<br/>[スピリチュアルリング]と<br/>共に装備時、追加で<br/>Dex + 2 , Matk + 10%<br/>不死形・悪魔形モンスター<br/>から受けるダメージ - 10%<br/>―――――――――――――<br/>[ディバイングローブ]と<br/>共に装備時、物理攻撃命中時<br/>習得している<br/>[ターンアンデッド]が<br/>Lv2以上の場合、習得Lvの<br/>[ターンアンデッド]が発動<br/>[ディバインクロス]の<br/>精錬値が5以上の時、<br/>精錬値が1上がる度に<br/>[ターンアンデッド]の<br/>発動率が上昇<br/>―――――――――――――<br/>系列 : <font color="777777">両手杖</font><br/>攻撃 : <font color="777777">120</font><br/>重量 : <font color="777777">150</font><br/>属性 : <font color="777777">聖</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">プリースト系 モンク系</font>');
REPLACE INTO `cp_itemdesc` VALUES(13304, '装備者の潜在能力を覚醒<br/>させる効果がある手裏剣。<br/>装備者に揺るがない<br/>強い精神力を与えてくれる。<br/>―――――――――――――<br/>詠唱が中断されない。<br/>風魔手裏剣投げ<br/>で与えるダメージ + 30%<br/>―――――――――――――<br/>系列 : <font color="777777">手裏剣</font><br/>攻撃 : <font color="777777">112</font><br/>重量 : <font color="777777">155</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">忍者系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1420, '聖なる一角獣の角で作られた<br/>槍で、解毒能力を持つ。<br/>とても鋭い為、一度<br/>刺されると血が止まらなく<br/>なると言われる。<br/>―――――――――――――<br/>物理攻撃時、一定確率で<br/>敵を出血状態にする<br/>―――――――――――――<br/>[解毒]Lv1使用可能<br/>―――――――――――――<br/>[ヴァルキリーシールド]と<br/>共に装備時、追加で<br/>物理攻撃を受けた時、<br/>一定確率で<br/>自分にオートスペル<br/>[アスムプティオ]Lv2発動<br/>―――――――――――――<br/>絶対に壊れない<br/>―――――――――――――<br/>系列 : <font color="777777">槍</font><br/>攻撃 : <font color="777777">150</font><br/>重量 : <font color="777777">100</font><br/>属性 : <font color="777777">聖</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">65</font><br/>装備 : <font color="777777">転生ソードマン系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1422, '狩りの為に作られた槍で、<br/>先に付いている刃が<br/>葉っぱの形をしている。<br/>刺す事より、重さを利用して<br/>殴る事を優先させている。<br/>―――――――――――――<br/>動物形モンスターの<br/>防御力無視。<br/>動物形モンスターを倒した時<br/>一定確率でにくをドロップ。<br/>物理攻撃時、一定確率で<br/>ジョイントビートLv3発動。<br/>―――――――――――――<br/>系列 : <font color="777777">槍</font><br/>攻撃 : <font color="777777">180</font><br/>重量 : <font color="777777">420</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">60</font><br/>装備 : <font color="777777">転生ソードマン系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1564, 'プロンテラのある出版社が<br/>発刊した大百科事典。<br/>角がとても硬く、<br/>ダイヤモンド3カラットでも<br/>仕込んであるのではないかと<br/>噂されている。<br/>―――――――――――――<br/>Int + 3 , Dex + 2<br/>Matk + 15%<br/>Cri + 20<br/>―――――――――――――<br/>純粋なLukが高い程、追加で<br/>Criが増加<br/>―――――――――――――<br/>[大百科事典改訂版]と<br/>共に装備時、<br/>[大百科事典]の<br/>精錬値が1上がる度に追加で<br/>攻撃速度 + 1%<br/>─────────────<br/>絶対に壊れない<br/>─────────────<br/>系列 : <font color="777777">本</font><br/>攻撃 : <font color="777777">110</font><br/>重量 : <font color="777777">200</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">プリースト系 セージ系 拳聖</font>');
REPLACE INTO `cp_itemdesc` VALUES(1565, '死神の名簿。<br/>極希にだが、世の中に<br/>死神が落とした名簿が<br/>出回る事があるらしい。<br/>中には人々の名前がギッシリ<br/>書かれており、<br/>見覚えのある名も見える。<br/>ホルグ……<br/><ページが破れているので、<br/>これ以上は読めません。><br/>―――――――――――――<br/>Matk + 15%<br/>―――――――――――――<br/>Str + 3 , Int + 3<br/>Luk - 20<br/>―――――――――――――<br/>物理攻撃命中時、<br/>一定確率で<br/>人間形モンスターに<br/>コーマ発動<br/>―――――――――――――<br/>[死神の名簿]の<br/>精錬値が1上がる度に追加で<br/>攻撃速度 + 1%<br/>―――――――――――――<br/>セージ系が装備時、<br/>物理攻撃命中時、<br/>一定確率で敵を中心に<br/>29x29セルの敵に<br/>一定のダメージを与え<br/>呪い状態にする<br/>―――――――――――――<br/>絶対に壊れない<br/>―――――――――――――<br/>系列 : <font color="777777">本</font><br/>攻撃 : <font color="777777">137</font><br/>重量 : <font color="777777">100</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">85</font><br/>装備 : <font color="777777">プリースト系 セージ系 拳聖</font>');
REPLACE INTO `cp_itemdesc` VALUES(1624, '強力なアンデッドの頭蓋骨で<br/>作られた杖。<br/>杖から溢れる呪いのオーラは<br/>杖の持ち主に害を及ぼす<br/>全ての者に呪いをかけると<br/>言われる。<br/>―――――――――――――<br/>Matk + 20%。<br/>―――――――――――――<br/>Int + 1、Dex + 1。<br/>―――――――――――――<br/>物理攻撃を受けた時、<br/>低確率で画面内の<br/>全ての敵を呪い状態にする。<br/>--------------------------<br/>精錬値が高くなる程、<br/>呪い状態にする確率増加。<br/>--------------------------<br/>精錬値が 9以上の時、<br/>追加で<br/>MaxSP + 300、<br/>Matk + 3%。<br/>―――――――――――――<br/>系列 : <font color="777777">杖</font><br/>攻撃 : <font color="777777">60</font><br/>重量 : <font color="777777">80</font><br/>属性 : <font color="777777">不死</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">転生マジシャン系 転生アコライト系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1626, '特定の波動を放つ宝石が<br/>付けられているスタッフ。<br/>この宝石の波動には、<br/>相手の魔法防御力の一部を<br/>無効にする効果がある。<br/>―――――――――――――<br/>Matk + 15%<br/>―――――――――――――<br/>Int + 4<br/>―――――――――――――<br/>魔法攻撃時、<br/>敵の魔法防御力を10%無視<br/>―――――――――――――<br/>精錬値が高くなる程、<br/>無視する魔法防御力が増加<br/>―――――――――――――<br/>[マジカルブースター]<br/>と共に装備時、<br/>[スタッフオブピアーシング]<br/>の精錬値が1上がる度に<br/>追加で、Matk + 15<br/>―――――――――――――<br/>[マジカルブースター]<br/>と共に装備時、<br/>[スタッフオブピアーシング]<br/>の精錬値が10の時、追加で<br/>Matk + 50<br/>詠唱時間 - 10%<br/>―――――――――――――<br/>系列 : <font color="777777">杖</font><br/>攻撃 : <font color="777777">80</font><br/>重量 : <font color="777777">50</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">転生マジシャン系 転生アコライト系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1818, '溶岩で作ったナックル。<br/>時折発せられる熱気は、<br/>全てを溶かすように熱い。<br/>ナックルの内部には<br/>特殊な加工が施されており、<br/>熱気が装備者の肌に<br/>伝わらない。<br/>―――――――――――――<br/>物理攻撃時、低確率で自分に<br/>フレイムランチャーLv5<br/>発動。<br/>―――――――――――――<br/>系列 : <font color="777777">爪</font><br/>攻撃 : <font color="777777">80</font><br/>重量 : <font color="777777">65</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">75</font><br/>装備 : <font color="777777">プリースト系 モンク系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1819, '氷柱で作ったナックル。<br/>時折発せられる冷気は、<br/>全てを凍らすように冷たい。<br/>ナックルの内部には<br/>特殊な加工が施されており、<br/>冷気が装備者の肌に<br/>伝わらない<br/>―――――――――――――<br/>物理攻撃時、低確率で自分に<br/>フロストウェポンLv5発動。<br/>―――――――――――――<br/>系列 : <font color="777777">爪</font><br/>攻撃 : <font color="777777">80</font><br/>重量 : <font color="777777">65</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">75</font><br/>装備 : <font color="777777">プリースト系 モンク系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1820, '稲妻の力を秘めたナックル。<br/>時折発せられる稲妻が、<br/>敵にとてつもない<br/>衝撃を与える。<br/>ナックルの内部には<br/>特殊な加工が施されており、<br/>電流が装備者の肌には<br/>伝わらない。<br/>―――――――――――――<br/>物理攻撃時、低確率で自分に<br/>ライトニングローダーLv5<br/>発動。<br/>―――――――――――――<br/>系列 : <font color="777777">爪</font><br/>攻撃 : <font color="777777">80</font><br/>重量 : <font color="777777">65</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">75</font><br/>装備 : <font color="777777">プリースト系 モンク系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1821, '大地の力を秘めたナックル。<br/>時折発せられる大地の力は、<br/>どんな敵でも倒せる程強力。<br/>敵には厳しい大地の力が、<br/>装備者には大地の女神の<br/>優しい愛となる。<br/>―――――――――――――<br/>物理攻撃時、低確率で自分に<br/>サイズミックウェポンLv5<br/>発動。<br/>―――――――――――――<br/>系列 : <font color="777777">爪</font><br/>攻撃 : <font color="777777">80</font><br/>重量 : <font color="777777">65</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">75</font><br/>装備 : <font color="777777">プリースト系 モンク系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1822, '刻まれた祝福の呪文が<br/>神の使徒たるモンクの意志と<br/>共鳴し、より強い攻撃を<br/>可能にしてくれる。<br/>修行の道を歩むモンクが<br/>これを装備すると、<br/>神に対する強い信仰が<br/>貫けるだろう。<br/>―――――――――――――<br/>三段掌・連打掌<br/>で与えるダメージ + 15%<br/>猛龍拳<br/>で与えるダメージ + 20%<br/>―――――――――――――<br/>系列 : <font color="777777">爪</font><br/>攻撃 : <font color="777777">30</font><br/>重量 : <font color="777777">50</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">60</font><br/>装備 : <font color="777777">プリースト系 モンク系</font>');
REPLACE INTO `cp_itemdesc` VALUES(1919, '低音でありながら、<br/>豊富な音量を奏でられる<br/>楽器。<br/>重厚な音が楽しめる。<br/>―――――――――――――<br/>敵を倒した時、SP3回復。<br/>―――――――――――――<br/>物理攻撃を受けた時、<br/>一定確率で<br/>ヘヴンズドライブLv3発動。<br/>―――――――――――――<br/>物理攻撃時、一定確率で<br/>自分を中心として<br/>5x5セル範囲内の全ての敵を<br/>混乱状態にする。<br/>―――――――――――――<br/>系列 : <font color="777777">楽器</font><br/>攻撃 : <font color="777777">130</font><br/>重量 : <font color="777777">150</font><br/>武器レベル : <font color="777777">3</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">クラウン ミンストレル</font>');
