REPLACE INTO `cp_itemdesc` VALUES(1972, '強力な電気エネルギーを放つ<br/>生き物で作った鞭。<br/>ぬらぬらしていて、<br/>気持ち悪い。<br/>―――――――――――――<br/>Agi+2<br/>Int+2<br/>―――――――――――――<br/>物理攻撃時、一定確率で<br/>ユピテルサンダーLv3発動。<br/>--------------------------<br/>精錬値が高くなる程、追加で<br/>　物理攻撃時、一定確率で<br/>　アローバルカンが高Lvで<br/>　発動。<br/>―――――――――――――<br/>系列 : <font color="777777">鞭</font><br/>攻撃 : <font color="777777">100</font><br/>重量 : <font color="777777">200</font><br/>属性 : <font color="777777">風</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">ジプシー ワンダラー</font>');
REPLACE INTO `cp_itemdesc` VALUES(1973, '海の魔女の足で作ったと<br/>いわれる鞭。敵の精気を奪う<br/>力があると言われている。<br/>―――――――――――――<br/>敵を倒した時、SP5回復。<br/>物理攻撃を受けた時、<br/>一定確率で<br/>フロストノヴァLv3発動。<br/>物理攻撃時、一定確率で<br/>自分を中心として、<br/>5x5セル範囲内の全ての敵を<br/>沈黙状態にする。<br/>―――――――――――――<br/>系列 : <font color="777777">鞭</font><br/>攻撃 : <font color="777777">110</font><br/>重量 : <font color="777777">150</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">ジプシー ワンダラー</font>');
