REPLACE INTO `cp_itemdesc` VALUES(13027, '小さくて軽い上、<br/>とても鋭い刃を持つ。<br/>剃刀のような刃は、<br/>人の肌を切開するのに<br/>最適だ。<br/>―――――――――――――<br/>物理攻撃命中時、<br/>敵を一定確率で<br/>出血状態にする<br/>―――――――――――――<br/>[オペレーションコート]と<br/>共に装備時、<br/>人間形・動物形モンスターを<br/>倒した時<br/>一定確率で<br/>[止まらない心臓]、<br/>[アルコール]ドロップ<br/>[オペレーションコート]の<br/>精錬値が1上がる度に、<br/>人間形・動物形モンスターを<br/>倒した時<br/>[止まらない心臓]、<br/>[アルコール]が<br/>ドロップする確率が上昇する<br/>―――――――――――――<br/>[オペレーショングローブ]と<br/>共に装備時、追加で<br/>攻撃速度 + 4%<br/>Atk + 20<br/>―――――――――――――<br/>[オペレーショングローブ]、<br/>[オペレーションコート]と<br/>共に装備時、<br/>人間形・動物形モンスターを<br/>倒した時<br/>[止まらない心臓]、<br/>[アルコール]が<br/>ドロップする確率が上昇する<br/>―――――――――――――<br/>系列 : <font color="777777">短剣</font><br/>攻撃 : <font color="777777">120</font><br/>重量 : <font color="777777">50</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">55</font><br/>装備 : <font color="777777">転生ソードマン系 転生マジシャン系 転生アーチャー系 転生マーチャント系 転生シーフ系</font>');
REPLACE INTO `cp_itemdesc` VALUES(13405, '軽く弾性の高い合金で<br/>作られた薄い剣。<br/>―――――――――――――<br/>攻撃速度 + 10%<br/>物理攻撃時、一定確率で<br/>敵を呪い状態にする。<br/>―――――――――――――<br/>系列 : <font color="777777">剣</font><br/>攻撃 : <font color="777777">125</font><br/>重量 : <font color="777777">80</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">55</font><br/>装備 : <font color="777777">転生ソードマン系 転生マーチャント系 転生シーフ系</font>');
