REPLACE INTO `cp_itemdesc` VALUES(2123, '料理を運ぶ時に使われる<br/>円形の鉄板。<br/>良く磨かれており、<br/>輝いている。<br/>―――――――――――――<br/>Mdef + 2<br/>―――――――――――――<br/>ターゲッティング魔法を<br/>5%の確率で反射<br/>―――――――――――――<br/>[オルレアンの手袋]と<br/>共に装備時、追加で<br/>詠唱時間 - 10%<br/>―――――――――――――<br/>[オルレアンネックレス]と<br/>共に装備時、追加で<br/>[オルレアンサーバー]の<br/>精錬値が高くなる程、<br/>Matkが増加<br/>―――――――――――――<br/>系列 : <font color="777777">盾</font><br/>防御 : <font color="777777">5</font><br/>重量 : <font color="777777">100</font><br/>要求レベル : <font color="777777">55</font><br/>装備 : <font color="777777">転生1次職 上位2次職 3次職</font>');
REPLACE INTO `cp_itemdesc` VALUES(2124, '表面が鋭いトゲで<br/>覆われている盾。<br/>攻撃をしてくる敵が可哀想に<br/>思えるくらいだ。<br/>―――――――――――――<br/>Mdef + 2<br/>―――――――――――――<br/>[野牛の角]と<br/>共に装備時、追加で<br/>攻撃速度 + 10%<br/>近接物理攻撃で<br/>受けたダメージの5%を反射<br/>―――――――――――――<br/>[ソーンシューズ]と<br/>共に装備時、<br/>[ソーンシールド]の<br/>精錬値が3上がる度に追加で<br/>攻撃速度 + 6%<br/>―――――――――――――<br/>系列 : <font color="777777">盾</font><br/>防御 : <font color="777777">5</font><br/>重量 : <font color="777777">100</font><br/>要求レベル : <font color="777777">55</font><br/>装備 : <font color="777777">転生1次職 上位2次職 3次職</font>');
