REPLACE INTO `cp_itemdesc` VALUES(2423, 'どこか不思議な雰囲気を<br/>かもし出すシューズ。<br/>神への挑戦の意味で<br/>製作されたが、<br/>失敗作らしい……<br/>―――――――――――――<br/>MaxHP・MaxSP + 20%<br/>--------------------------<br/>精錬値が高くなるほど、<br/>追加で<br/>MaxHP・MaxSPが減少。<br/>Defが増加。<br/>―――――――――――――<br/>系列 : <font color="777777">靴</font><br/>防御 : <font color="777777">3</font><br/>重量 : <font color="777777">50</font><br/>要求レベル : <font color="777777">85</font><br/>装備 : <font color="777777">転生1次職 上位2次職 3次職</font>');
