REPLACE INTO `cp_itemdesc` VALUES(2748, '呪われた星の下で生まれた<br/>職工が自分の呪いを<br/>避けるために作った<br/>魔除けのネックレス。<br/>呪いの力を封印するための<br/>さまざまな力が宿っている。<br/>―――――――――――――<br/>Mdef + 3<br/>―――――――――――――<br/>Dex + 2 , Luk - 1<br/>―――――――――――――<br/>遠距離物理攻撃命中時、<br/>2%の確率で敵を<br/>呪い状態にする<br/>―――――――――――――<br/>装備時、10秒毎にHP - 50<br/>装備解除時、HP - 300<br/>―――――――――――――<br/>[カーズドライヤ]と<br/>共に装備時、追加で<br/>Atk + 25<br/>遠距離物理攻撃命中時、<br/>4%の確率で敵を<br/>呪い状態にする<br/>―――――――――――――<br/>系列 : <font color="777777">アクセサリー</font><br/>防御 : <font color="777777">0</font><br/>重量 : <font color="777777">20</font><br/>要求レベル : <font color="777777">84</font><br/>装備 : <font color="777777">転生アーチャー系</font>');
