REPLACE INTO `cp_itemdesc` VALUES(1186, 'あらゆる存在を破壊する為に<br/>存在する呪われた武器。<br/>武器の呪いは持ち主までも<br/>不幸にさせると言う。<br/>―――――――――――――<br/>STR + 5、AGI + 2<br/>完全回避 + 20<br/>物理攻撃時、一定確率で<br/>自分と敵に対し死亡時に<br/>数分間復活ができない<br/>状態異常がかかる。<br/>物理攻撃時、一定確率で<br/>自分を中心に11*11セル<br/>の中のすべてのモンスターに<br/>100%のダメージを与えつつ、<br/>与えたダメージの100%のHPを<br/>回復する。<br/>精錬値が9以上の場合、<br/>15*15セルの中のすべての<br/>モンスターに200%のダメージ<br/>を与えつつHPを回復する。<br/>―――――――――――――<br/>系列 : <font color="777777">両手剣</font><br/>攻撃 : <font color="777777">200</font><br/>重量 : <font color="777777">200</font><br/>武器レベル : <font color="777777">4</font><br/>要求レベル : <font color="777777">70</font><br/>装備 : <font color="777777">転生ソードマン系</font>');
